import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider, connectAuthEmulator } from 'firebase/auth';
import { getFirestore, connectFirestoreEmulator } from 'firebase/firestore';
import { getFunctions, connectFunctionsEmulator } from 'firebase/functions';

// Firebase config - actual project configuration
const firebaseConfig = {
  apiKey: "AIzaSyAIeeqRAqZSKJzFr5GnfgIvpjwfYU9z_OU",
  authDomain: "job-tracker-bd6dc.firebaseapp.com",
  projectId: "job-tracker-bd6dc",
  storageBucket: "job-tracker-bd6dc.firebasestorage.app",
  messagingSenderId: "831968228747",
  appId: "1:831968228747:web:c2e75da41131ded1a12b8e",
  measurementId: "G-X8T9QY9VBJ"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firebase services
export const auth = getAuth(app);
export const db = getFirestore(app);
export const functions = getFunctions(app);

// Configure Google Auth Provider
export const googleProvider = new GoogleAuthProvider();
googleProvider.addScope('https://www.googleapis.com/auth/gmail.readonly');

// Connect to emulators in development
const useEmulators = process.env.REACT_APP_USE_EMULATORS === 'true';
const isEmulatorConnected = {
  auth: false,
  firestore: false,
  functions: false
};

if (useEmulators) {
  try {
    // Connect to Auth emulator
    if (!isEmulatorConnected.auth) {
      connectAuthEmulator(auth, `http://localhost:9099`, { disableWarnings: true });
      isEmulatorConnected.auth = true;
      console.log('‚úÖ Connected to Firebase Auth emulator');
    }
  } catch (error) {
    console.log('‚ö†Ô∏è Auth emulator connection failed:', error.message);
  }

  try {
    // Connect to Firestore emulator
    if (!isEmulatorConnected.firestore) {
      connectFirestoreEmulator(db, 'localhost', 8080);
      isEmulatorConnected.firestore = true;
      console.log('‚úÖ Connected to Firestore emulator');
    }
  } catch (error) {
    console.log('‚ö†Ô∏è Firestore emulator connection failed:', error.message);
  }

  try {
    // Connect to Functions emulator
    if (!isEmulatorConnected.functions) {
      connectFunctionsEmulator(functions, 'localhost', 5001);
      isEmulatorConnected.functions = true;
      console.log('‚úÖ Connected to Firebase Functions emulator');
    }
  } catch (error) {
    console.log('‚ö†Ô∏è Functions emulator connection failed:', error.message);
  }

  console.log('üîß Development mode: Using Firebase emulators');
} else {
  console.log('üöÄ Production mode: Using Firebase services');
}

export default app;
